const createMonthChart=function(t){return new Chart(document.getElementById(t),{type:"bar",plugins:[{afterDraw:t=>{const r=t.ctx;r.save();const o=t.scales.x,e=t.scales.y.getPixelForValue(0);r.beginPath(),r.moveTo(o.left,e),r.lineTo(o.right,e),r.strokeStyle="rgba(255, 255, 255, 0.8)",r.stroke(),r.restore()}}],data:{datasets:[{label:"Авторизация",data:[{x:0,y:0}],backgroundColor:function(t){const r=t.dataIndex;return t.dataset.data[r].y<0?"rgba(220, 53, 69, 1)":"rgba(25, 135, 84, 1)"},borderColor:function(t,r){const o=r.color;return Chart.helpers.color(o).lighten(.2)},color:"rgba(255, 255, 255, 0.5)"}]},options:{elements:{point:{radius:0}},plugins:{legend:{labels:{color:"rgba(255, 255, 255, 0.5)",boxWidth:0}}},scales:{y:{grid:{color:"rgba(255, 255, 255, 0.3)",borderColor:"rgba(255, 255, 255, 0.3)"},color:"rgba(255, 255, 255, 0.5)",min:-10,suggestedMax:100,title:{color:"rgba(255, 255, 255, 0.5)",display:!0,text:"Задержка, ms"},ticks:{color:"rgba(255, 255, 255, 0.5)",callback:function(t,r){return t>=0?t:"down"}}},x:{ticks:{color:"rgba(255, 255, 255, 0.5)"},grid:{color:"rgba(255, 255, 255, 0.3)"},type:"time",time:{displayFormats:{hour:"HH:mm"},tooltipFormat:"DD.MM.yyyy",unit:"day",round:"day"}}}}})};
